import React, { useState, useEffect } from "react";
import { TextField, Button, Typography, Paper } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import FileBase from "react-file-base64";
import { useHistory } from "react-router-dom";
import ChipInput from "material-ui-chip-input";

import { createPost, updatePost } from "../../actions/posts";
import useStyles from "./styles";
import { purple, pink, orange, blueGrey } from "@mui/material/colors";
import { styled } from "@mui/material/styles";

const ColorButton = styled(Button)(({ theme }) => ({
    color: theme.palette.getContrastText(purple[500]),
    backgroundColor: pink[800],
    "&:hover": {
        backgroundColor: orange[600],
    },
}));

const ClearButton = styled(Button)(({ theme }) => ({
    color: theme.palette.getContrastText(blueGrey[500]),
    backgroundColor: blueGrey[900],
    "&:hover": {
        backgroundColor: blueGrey[500],
    },
}));

const Form = ({ currentId, setCurrentId }) => {
    const [postData, setPostData] = useState({
        title: "",
        message: "",
        tags: [],
        selectedFile: "",
    });
    const post = useSelector((state) =>
        currentId
            ? state.posts.posts.find((message) => message._id === currentId)
            : null
    );
    const dispatch = useDispatch();
    const classes = useStyles();
    const user = JSON.parse(localStorage.getItem("profile"));
    const history = useHistory();

    const clear = () => {
        setCurrentId(0);
        setPostData({ title: "", message: "", tags: [], selectedFile: "" });
    };

    useEffect(() => {
        if (!post?.title) clear();
        if (post) setPostData(post);
    }, [post]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (currentId === 0) {
            dispatch(
                createPost({ ...postData, name: user?.result?.name }, history)
            );
            clear();
        } else {
            dispatch(
                updatePost(currentId, { ...postData, name: user?.result?.name })
            );
            clear();
        }
    };

    if (!user?.result?.name) {
        return (
            <Paper className={classes.paper} elevation={6}>
                <Typography variant="h6" align="center">
                    Please Sign In to create your own Travel post and like
                    other's post.
                </Typography>
            </Paper>
        );
    }

    const handleAddChip = (tag) => {
        setPostData({ ...postData, tags: [...postData.tags, tag] });
    };

    const handleDeleteChip = (chipToDelete) => {
        setPostData({
            ...postData,
            tags: postData.tags.filter((tag) => tag !== chipToDelete),
        });
    };

    return (
        <Paper className={classes.paper} elevation={6}>
            
            <form
                autoComplete="off"
                noValidate
                className={`${classes.root} ${classes.form}`}
                onSubmit={handleSubmit}
                style={{display: "flex",flexW}}
            >
                <div
                    style={{
                        display: "flex",
                        flexWrap: "wrap",
                        justifyContent: "space-around",
                    }}
                >
                    <div>
                        <Typography variant="h6">
                            {currentId
                                ? `Editing "${post?.title}"`
                                : "Creating a Post"}
                        </Typography>
                        <TextField
                            name="title"
                            variant="outlined"
                            label="Headline"
                            fullWidth
                            value={postData.title}
                            onChange={(e) =>
                                setPostData({
                                    ...postData,
                                    title: e.target.value,
                                })
                            }
                        />
                        <TextField
                            name="message"
                            variant="outlined"
                            label="Whats on your mind?"
                            fullWidth
                            multiline
                            rows={4}
                            value={postData.message}
                            onChange={(e) =>
                                setPostData({
                                    ...postData,
                                    message: e.target.value,
                                })
                            }
                        />
                        <div style={{ padding: "5px 0", width: "98%" }}>
                            <ChipInput
                                name="tags"
                                variant="outlined"
                                label="Tags"
                                fullWidth
                                value={postData.tags}
                                onAdd={(chip) => handleAddChip(chip)}
                                onDelete={(chip) => handleDeleteChip(chip)}
                            />
                        </div>
                        <div className={classes.fileInput}>
                            <FileBase
                                type="file"
                                multiple={false}
                                onDone={({ base64 }) =>
                                    setPostData({
                                        ...postData,
                                        selectedFile: base64,
                                    })
                                }
                            />
                        </div>
                    </div>

                    <div>
                        <ColorButton
                            className={classes.buttonSubmit}
                            variant="contained"
                            color="primary"
                            size="large"
                            type="submit"
                            fullWidth
                        >
                            Submit
                        </ColorButton>
                        <ClearButton
                            variant="contained"
                            color="secondary"
                            size="small"
                            onClick={clear}
                            fullWidth
                        >
                            Clear
                        </ClearButton>
                    </div>
                </div>
            </form>
        </Paper>
    );
};

export default Form;
